BEGIN BEGIN TRY SET NOCOUNT ON;
DECLARE @CurrentDate AS DATETIME = GETDATE()
--Merge the Permissions Details. MERGE dbo.HSNTaxes AS T USING (SELECT @HSNCodeId AS HSNCodeId ,TaxId ,FromDate ,ToDate ,FromRate ,ToRate ,FromPurcRate ,ToPurcRate FROM @HSNTaxType) AS S ON T.HSNCodeId = S.HSNCodeId AND T.TaxId= S.TaxId WHEN NOT MATCHED BY TARGET THEN --Insert Rows. INSERT ( [HSNCodeId] ,[TaxId] ,[FromDate] ,[ToDate] ,[FromRate] ,[ToRate] ,[FromPurcRate] ,[ToPurcRate] ,[CreatedBy] ,[CreatedDate] ,[UpdatedBy] ,[UpdatedDate] ) VALUES ( HSNCodeId ,TaxId ,FromDate ,ToDate ,FromRate ,ToRate ,FromPurcRate ,ToPurcRate ,@UpdatedBy ,@CurrentDate ,@UpdatedBy ,@CurrentDate ) WHEN MATCHED THEN --Update Rows. UPDATE SET
T.FromDate = s.FromDate ,T.ToDate = s.ToDate ,T.FromRate = s.FromRate ,T.ToRate = s.ToRate ,T.FromPurcRate = s.FromPurcRate ,T.ToPurcRate = s.ToPurcRate ,T.UpdatedBy = @UpdatedBy ,T.UpdatedDate = GETDATE() WHEN NOT MATCHED BY SOURCE AND T.HSNCodeId = @HSNCodeId THEN --Delete Rows. DELETE;
RETURN 0 END TRY BEGIN CATCH RETURN @@ERROR END CATCH END

BEGIN BEGIN TRY SET NOCOUNT ON;
DECLARE @CurrentDate AS DATETIME = GETDATE()
--Merge the Permissions Details. MERGE dbo.ProductSizes AS T USING (SELECT @ProductId AS ProductId ,SizeShortName ,SrNo FROM @ProductSizeType) AS S ON T.SrNo = S.SrNo AND T.ProductId= S.ProductId WHEN NOT MATCHED BY TARGET THEN --Insert Rows. INSERT ( [ProductId] ,[SizeShortName] ,[SrNo] ,[CreatedBy] ,[CreatedDate] ,[UpdatedBy] ,[UpdatedDate] ) VALUES ( ProductId ,SizeShortName ,SrNo ,@UpdatedBy ,@CurrentDate ,@UpdatedBy ,@CurrentDate ) WHEN MATCHED THEN --Update Rows. UPDATE SET
T.SizeShortName = S.SizeShortName ,T.UpdatedBy = @UpdatedBy ,T.UpdatedDate = GETDATE() WHEN NOT MATCHED BY SOURCE AND T.ProductId = @ProductId THEN --Delete Rows. DELETE;
RETURN 0 END TRY BEGIN CATCH RETURN @@ERROR END CATCH END

BEGIN BEGIN TRY SET NOCOUNT ON;
--Merge the Permissions Details. MERGE dbo.RolePermissions AS T USING (SELECT @RoleId AS RoleId, MenuId, PermissionId, HasPermission FROM @RolePermissionType) AS S ON T.RoleId = S.RoleId AND T.PermissionId = S.PermissionId AND T.MenuId = S.MenuId WHEN NOT MATCHED BY TARGET THEN --Insert Rows. INSERT ( [RoleId] ,[MenuId] ,[PermissionId] ,[HasPermission] ) VALUES ( S.RoleId ,S.MenuId ,S.PermissionId ,S.HasPermission ) WHEN MATCHED THEN --Update Rows. UPDATE SET HasPermission = S.HasPermission WHEN NOT MATCHED BY SOURCE AND T.RoleId = @RoleId THEN --Delete Rows. DELETE;
RETURN 0 END TRY BEGIN CATCH RETURN @@ERROR END CATCH END

BEGIN BEGIN TRY SET NOCOUNT ON;
DECLARE @CurrentDate AS DATETIME = GETDATE()
--Merge the Permissions Details. MERGE dbo.SizeCountries AS T USING (SELECT @ShortName AS ShortName ,SizeDepartmentId ,USSizeShortName ,EuropSizeShortName ,LengthCM ,LengthInch FROM @SizeCountryType) AS S ON T.SizeShortName = S.ShortName AND T.SizeDepartmentId= S.SizeDepartmentId WHEN NOT MATCHED BY TARGET THEN --Insert Rows. INSERT ( [SizeShortName] ,[SizeDepartmentId] ,[USSizeShortName] ,[EuropSizeShortName] ,[LengthCM] ,[LengthInch] ,[CreatedBy] ,[CreatedDate] ,[UpdatedBy] ,[UpdatedDate] ) VALUES ( ShortName ,SizeDepartmentId ,USSizeShortName ,EuropSizeShortName ,LengthCM ,LengthInch ,@UpdatedBy ,@CurrentDate ,@UpdatedBy ,@CurrentDate ) WHEN MATCHED THEN --Update Rows. UPDATE SET T.USSizeShortName = S.USSizeShortName ,T.EuropSizeShortName = S.EuropSizeShortName ,T.LengthCM = S.LengthCM ,T.LengthInch = S.LengthInch ,T.UpdatedBy = @UpdatedBy ,T.UpdatedDate = GETDATE() WHEN NOT MATCHED BY SOURCE AND T.SizeShortName = @ShortName THEN --Delete Rows. DELETE;
RETURN 0 END TRY BEGIN CATCH RETURN @@ERROR END CATCH END

BEGIN BEGIN TRY SET NOCOUNT ON;
DECLARE @CurrentDate AS DATETIME = GETDATE()
--Merge the Permissions Details. MERGE dbo.SizeGroupSizes AS T USING (SELECT @SizeGroupId AS SizeGroupId ,SizeShortName ,SrNo FROM @SizeGroupSizeType) AS S ON T.SrNo = S.SrNo AND T.SizeGroupId= S.SizeGroupId WHEN NOT MATCHED BY TARGET THEN --Insert Rows. INSERT ( [SizeGroupId] ,[SizeShortName] ,[SrNo] ,[CreatedBy] ,[CreatedDate] ,[UpdatedBy] ,[UpdatedDate] ) VALUES ( SizeGroupId ,SizeShortName ,SrNo ,@UpdatedBy ,@CurrentDate ,@UpdatedBy ,@CurrentDate ) WHEN MATCHED THEN --Update Rows. UPDATE SET
T.SizeShortName = S.SizeShortName ,T.UpdatedBy = @UpdatedBy ,T.UpdatedDate = GETDATE() WHEN NOT MATCHED BY SOURCE AND T.SizeGroupId = @SizeGroupId THEN --Delete Rows. DELETE;
RETURN 0 END TRY BEGIN CATCH RETURN @@ERROR END CATCH END
